package basics1;

public class EvenOrOdd {

	public static void main(String[] args) {
		evenOrOdd(367);
	}
	static void evenOrOdd(int i) {
		if(i%2==0) {
			System.out.println("Number Is Even");
		}
		else;{
			System.out.println("Number Is Odd");
		}
		// TODO Auto-generated method stub

	}

}
